package com.learner.unicom;import java.math.BigInteger;import java.security.MessageDigest;import java.util.UUID;/** * Created by liufangliang on 2018/1/11. */public class AihuishouUtils {    public final static String APPID="10001";    public final static String APPKEY="254aa0608a8f4012bf7170bfe12b0f7a";    public final static String TOKEN="91e8c7a6-161a-445b-b463-d35c3348f1eb";    public  String getMD5(String input) {        try {            String bigInteger = new BigInteger(1, MessageDigest.getInstance("MD5").digest(input.getBytes())).toString(16);            while (bigInteger.length() < 32) {                bigInteger = "0" + bigInteger;            }            return bigInteger;        } catch (Throwable e) {            throw new RuntimeException(e);        }    }    public String getToken (){        UUID randomUUID=UUID.randomUUID();        System.out.println(randomUUID);    return randomUUID.toString();    }    public static void main(String[] args) {        AihuishouUtils ai=new AihuishouUtils();        long cuurent=System.currentTimeMillis();        System.out.println(cuurent);        System.out.println(cuurent/1000);        ai.getToken();        String sign = ai.getMD5(AihuishouUtils.APPID+AihuishouUtils.APPKEY+cuurent/1000+"");        System.out.println(sign);    }}